class _mainpageState extends State<mainpage> {
  var cindex = 0;
  final screens = [
    const IGcard(),
    const Center(
        child: Text('search',
            style: TextStyle(color: Colors.white, fontSize: 50))),
    const Center(
        child:
            Text('camera', style: TextStyle(color: Colors.white, fontSize: 50)))
  ];
  @override
  Widget build(BuildContext context) => Scaffold(
        appBar: AppBar(
          title: const Text('instagram'),
          backgroundColor: Colors.black87,
        ),
        body: screens[cindex],
        bottomNavigationBar: BottomNavigationBar(
          type: BottomNavigationBarType.shifting,
          currentIndex: cindex,
          onTap: (index) => setState(() => cindex = index),
          items: const [
            BottomNavigationBarItem(
                icon: Icon(Icons.home),
                label: 'home',
                backgroundColor: Colors.blue),
            BottomNavigationBarItem(
                icon: Icon(Icons.search),
                label: 'search',
                backgroundColor: Colors.red),
            BottomNavigationBarItem(
                icon: Icon(Icons.camera),
                label: 'camera',
                backgroundColor: Colors.purple)
          ],
        ),
        backgroundColor: Colors.black87,
      );
}

class IGcard extends StatelessWidget {
  const IGcard({Key? key}) : super(key: key);

  @override
  Widget build(BuildContext context) {
    return ListView.separated(
      itemBuilder: (context, index) => Igcard(postContent[index]),
      separatorBuilder: (context, index) => Container(
        width: double.infinity,
        height: 1,
        color: Colors.grey,
      ),
      itemCount: postContent.length,
    );
  }
}

Widget Igcard(Posts posts) => Padding(
      padding: const EdgeInsets.all(10),
      child: Column(
        crossAxisAlignment: CrossAxisAlignment.start,
        mainAxisSize: MainAxisSize.min,
        children: [
          Row(
            children: [
              Padding(
                padding: EdgeInsets.all(10.0),
                child: CircleAvatar(
                    backgroundImage: NetworkImage(posts.userImage)),
              ),
              SizedBox(
                width: 5,
              ),
              Text(
                posts.userName,
                style: TextStyle(color: Colors.white),
              )
            ],
          ),
          Image.network(
            posts.postImage,
            height: 300,
            width: double.infinity,
            fit: BoxFit.cover,
          ),
          Row(
            children: [
              const Icon(
                Icons.favorite,
                color: Colors.white,
                size: 45,
              ),
              const Icon(
                Icons.place,
                color: Colors.white,
                size: 45,
              ),
              Expanded(child: Container()),
              const Icon(
                Icons.more_vert,
                color: Colors.white,
                size: 45,
              )
            ],
          ),
          const Padding(
            padding: EdgeInsets.fromLTRB(10.0, 0.0, 0.0, 16.0),
            child: Text(
              '2131 Likes',
              style: TextStyle(
                  color: Colors.white,
                  fontSize: 25,
                  fontWeight: FontWeight.w800),
            ),
          ),
        ],
      ),
    );

class Posts {
  String postImage, userImage, userName, likes;

  Posts({
    required this.postImage,
    required this.userImage,
    required this.userName,
    required this.likes,
  });
}
